UPDATE locales_npc_text SET Text0_0_loc3='Aus mir unverständlichen Gründen, hält sich Urahne Donnerhorn momentan in der wuchernden Narbe im Krater von Un\'Goro auf.', Text0_1_loc3='Aus mir unverständlichen Gründen, hält sich Urahne Donnerhorn momentan in der wuchernden Narbe im Krater von Un\'Goro auf.' WHERE entry=8199;
UPDATE locales_npc_text SET Text0_0_loc3='Wenn Ihr zur Burg Cenarius in Silithus reist, werdet Ihr Urahnin Klingensang finden.', Text0_1_loc3='Wenn Ihr zur Burg Cenarius in Silithus reist, werdet Ihr Urahnin Klingensang finden.' WHERE entry=8200;
