UPDATE command SET help='Syntax: .debug update #field #value\r\n\r\nAktualisiert das Feld #field des angewählten Charakters oder der angewählten Kreatur auf den Wert #value.\r\n\r\nFalls kein #value angegeben wurde, wird der Inhalt des Feldes #field angezeigt.' WHERE name='debug update';
UPDATE command SET help='Syntax: .debug Mod32Value #field #value\r\n\r\nFügt #value zum Feld #field Ihres Charakter hinzu.' WHERE name='debug Mod32Value';
UPDATE command SET help='Syntax: .gm online\r\n\r\nZeigt eine Liste aller verfügbaren Gamemaster an.' WHERE name='gm online';
UPDATE command SET help='Syntax: .go trigger (#trigger_id|$trigger_shift-link|$trigger_target_shift-link) [target]\r\n\r\nTeleportiert Euren Charakter zur Areatigger mit der Id #tigger_id order zugehörigen tigger_id (mit/über shift-link). Wird der optionale Parameter "target" angegeben wird der Charakter zum Areatigger-Ziel ausgerichtet sein.' WHERE name='go tigger';
UPDATE command SET help='Syntax: .modify bit #field #bit\r\n\r\nÄndert das #bit Bit des Feldes #field für den angewählten Spieler. Falls kein Spieler angewählt ist, wird Euer Charakter modifiziert.' WHERE name='modify bit';
UPDATE command SET help='Syntax: .modify spell\r\n\r\nModifiziert einen Zauber (Spell) des angewählten Spielers. Falls kein Spieler angewählt ist, wird ein Zauber Eures Charakters modifiziert.' WHERE name='modify spell';
