UPDATE locales_quest SET Title_loc3='Aufgesammelte Kürbisse', Details_loc3='Hi, $N! Danke, dass Ihr den Reiter vertrieben habt! Jetzt, da er weg ist, hat uns die Matrone erlaubt, durchs Dorf zu ziehen!$B$BIch habe ein paar Kürbisse gefunden, die der Reiter fallen ließ. Die sehen unheimlich aus... würdet Ihr sie nehmen und der Matrone zeigen?', Objectives_loc3='Bringt das Bündel Kürbislaternen zur kostümierten Waisenmatrone.' WHERE entry=11435;
